// ===== Smart Desk Enhanced Simulator V2 ‚Äì With Desk Height Tracking =====
function rand(min, max) { return Math.round(Math.random() * (max - min) + min); }
function rfloat(min, max, d = 1) { return parseFloat((Math.random() * (max - min) + min).toFixed(d)); }

const REAL_TICK_SEC = 30;
const DEMO_MIN_PER_TICK = 3;
const TICK_SIM_SEC = DEMO_MIN_PER_TICK * 60;

// Desk height constants
const SITTING_HEIGHT_MIN = 72;
const SITTING_HEIGHT_MAX = 82;
const STANDING_HEIGHT_MIN = 105;
const STANDING_HEIGHT_MAX = 115;
const STANDING_THRESHOLD = 90;
const MIN_STAND_DURATION_SEC = 15 * 60; // 15 minutes in seconds

let state = flow.get("focusState") || {
    mode: "focus",
    remainingSec: 25 * 60,
    sitStreakSec: 0,
    standCyclesToday: 0,
    lastResetDate: null,
    wasStanding: true,
    standStartTime: null,
    lastStandingHeight: null
};

// Reset stand cycles every day
const today = new Date().toDateString();
if (!state.lastResetDate || state.lastResetDate !== today) {
    state.standCyclesToday = 0;
    state.lastResetDate = today;
    node.warn(`üåÖ New day started! Stand cycles reset.`);
}

// Advance Pomodoro-style timer
state.remainingSec -= TICK_SIM_SEC;
if (state.remainingSec <= 0) {
    if (state.mode === "focus") {
        state.mode = "break";
        state.remainingSec = 5 * 60;
    } else {
        state.mode = "focus";
        state.remainingSec = 25 * 60;
    }
}

// --- DESK HEIGHT LOGIC (Ground Truth!) ---
// --- DESK HEIGHT LOGIC (Ground Truth!) ---
let deskHeightCm;
const sitMinutes = state.sitStreakSec / 60;

// First, determine what the desk height SHOULD be based on sitting time
if (sitMinutes < 45) {
    // Phase 1: Normal sitting (before alert)
    if (state.lastStandingHeight !== null) {
        // Was standing, but sitting time < 45 means we sat down
        deskHeightCm = rand(SITTING_HEIGHT_MIN, SITTING_HEIGHT_MAX);
        state.lastStandingHeight = null; // Clear it since we're sitting
    } else {
        deskHeightCm = rand(SITTING_HEIGHT_MIN, SITTING_HEIGHT_MAX);
    }

} else if (sitMinutes < 50) {
    // Phase 2: Alert just triggered, user hasn't responded yet
    deskHeightCm = rand(SITTING_HEIGHT_MIN, SITTING_HEIGHT_MAX);
    state.lastStandingHeight = null;

} else if (sitMinutes < 60) {
    // Phase 3: Alert active, 30% chance user raises desk
    if (state.lastStandingHeight !== null) {
        // Already standing - keep the same height
        deskHeightCm = state.lastStandingHeight;
    } else if (Math.random() < 0.3) {
        // Randomly decide to stand up
        state.lastStandingHeight = rand(STANDING_HEIGHT_MIN, STANDING_HEIGHT_MAX);
        deskHeightCm = state.lastStandingHeight;
    } else {
        // Still sitting
        deskHeightCm = rand(SITTING_HEIGHT_MIN, SITTING_HEIGHT_MAX);
    }

} else {
    // Phase 4: Force standing after 60 min
    if (state.lastStandingHeight === null) {
        state.lastStandingHeight = rand(STANDING_HEIGHT_MIN, STANDING_HEIGHT_MAX);
    }
    deskHeightCm = state.lastStandingHeight;
}

// NOW derive sitting state FROM desk height (ground truth!)
let sitStandState = deskHeightCm > STANDING_THRESHOLD ? 1 : 0;

// --- Presence Logic (sticky while sitting) ---
let present;
if (state.sitStreakSec > 0 && sitStandState === 0) {
    // Currently sitting - stay present
    present = true;
} else {
    // Not sitting - allow leaving desk
    present = Math.random() < (state.mode === "focus" ? 0.8 : 0.3);
}

// --- NEW: Auto-reset sit streak after 15 minutes of standing ---
if (state.standStartTime !== null && sitStandState === 1) {
    const standDurationSec = Date.now()/1000 - state.standStartTime;

    if (standDurationSec >= MIN_STAND_DURATION_SEC && state.sitStreakSec > 0) {
        // User has been standing for 15+ minutes - auto-reset sit streak
        node.warn(`‚úÖ Stand duration reached ${(standDurationSec/60).toFixed(1)} min - Sit streak RESET!`);
        state.sitStreakSec = 0;
    }
}

// --- Enhanced Sitting Streak Logic with 15-min Stand Requirement ---
if (present && sitStandState === 0) {
    // User is sitting

    // Check if they were just standing
    if (state.wasStanding && state.standStartTime !== null) {
        // They just sat down after standing
        const standDurationSec = Date.now()/1000 - state.standStartTime;

        if (standDurationSec >= MIN_STAND_DURATION_SEC) {
            // User stood long enough - RESET sit streak
            state.sitStreakSec = TICK_SIM_SEC;
            node.warn(`ü™ë Sitting... Streak RESET ‚úÖ (stood for ${(standDurationSec/60).toFixed(1)} min) (Desk: ${deskHeightCm} cm)`);
        } else {
            // User didn't stand long enough - CONTINUE sit streak
            state.sitStreakSec += TICK_SIM_SEC;
            node.warn(`ü™ë Sitting again... Streak CONTINUES ‚ö†Ô∏è at ${Math.round(state.sitStreakSec / 60)} min (only stood ${(standDurationSec/60).toFixed(1)} min) (Desk: ${deskHeightCm} cm)`);
        }
        state.standStartTime = null; // Clear stand timer
        state.lastStandingHeight = null; // Clear standing height
    } else {
        // Continue sitting
        state.sitStreakSec += TICK_SIM_SEC;
        node.warn(`ü™ë Sitting... Streak: ${Math.round(state.sitStreakSec / 60)} min (Desk: ${deskHeightCm} cm)`);
    }

    state.wasStanding = false;

} else if (state.sitStreakSec > 0 && !state.wasStanding && sitStandState === 1) {
    // User just stood up (transition from sitting to standing)
    state.standStartTime = Date.now() / 1000; // Record when they started standing
    state.standCyclesToday += 1;
    state.wasStanding = true;
    state.lastStandingHeight = deskHeightCm; // Lock in this standing height
    node.warn(`‚¨ÜÔ∏è User stood up! Stand cycles: ${state.standCyclesToday} (Desk: ${deskHeightCm} cm) - Timer started!`);

} else if (state.sitStreakSec > 0 && sitStandState === 1) {
    // Still standing - keep the timer running
    state.wasStanding = true;
}

// Emit alert when sitting too long (FIX: only when actually sitting!)
if (state.sitStreakSec >= 45 * 60 && sitStandState === 0 && state.sitStreakSec % (10 * 60) < TICK_SIM_SEC) {
    node.warn(`üö® LONG SIT ALERT! sitStreakMin = ${Math.round(state.sitStreakSec / 60)}, deskHeight = ${deskHeightCm} cm`);
}

// --- Core sensor readings ---
const temperatureC = rfloat(22, 26, 1);
const lightLux = rand(150, 600);
const humidityPct = rand(35, 60);
const noiseDb = rand(42, 74);
const co2ppm = rand(500, 1400);
const lampOn = present && lightLux < 300;
const lampPowerW = lampOn ? (lightLux < 220 ? 9 : 6) : 0;
const focusScore = present ? (state.mode === "focus" ? rand(60, 95) : rand(30, 70)) : 0;
const keyRatePerMin = present ? (state.mode === "focus" ? rand(80, 220) : rand(10, 120)) : 0;
const screenOn = present ? 1 : 0;

// Posture (better when standing)
const postureAngleDeg = present ? (sitStandState === 0 ? rand(5, 35) : rand(0, 15)) : 0;
const postureGood = present ? (postureAngleDeg < 25) : 1;

const microbreakDue = (state.mode === "focus" && (state.remainingSec % (10 * 60) < TICK_SIM_SEC)) ? 1 : 0;

// --- Build payload ---
const payload = {
    deskId: "desk01",
    userId: "user01",
    appFocus: ["IDE", "Browser", "Mail", "Other"][rand(0, 3)],
    present: present,
    sitStandState: sitStandState,
    sitStreakMin: Math.round(state.sitStreakSec / 60),
    standCyclesToday: state.standCyclesToday,
    deskHeightCm,
    temperatureC,
    lightLux,
    humidityPct,
    noiseDb,
    co2ppm,
    lampOn,
    lampPowerW,
    screenOn,
    focusScore,
    keyRatePerMin,
    postureAngleDeg,
    postureGood,
    microbreakDue,
    focusTimerMin: Math.max(0, Math.round(state.remainingSec / 60)),
    mode: state.mode,
    ts: new Date().toISOString()
};

flow.set("focusState", state);

msg.topic = "desk/desk01/telemetry";
msg.payload = JSON.stringify(payload);
return msg;

let p = msg.payload;
if (typeof p === "string") {
    try { p = JSON.parse(p); }
    catch (e) { node.warn("Bad JSON in seating details: " + e.message); return null; }
}
if (!p || !p.deskId || !p.userId) return null;

const presentVal = p.present ? 1 : 0;

msg.measurement = "desk_seating";
msg.tags = {
    deskId: p.deskId,
    userId: p.userId,
    mode: p.mode || "unknown"
};

// *** Now build the InfluxDB payload ***
msg.payload = {
    present: presentVal,
    sitStandState: Number(p.sitStandState) || 0,   // 0=sit, 1=stand
    sitStreakMin: Number(p.sitStreakMin) || 0,
    standCyclesToday: Number(p.standCyclesToday) || 0,
    deskHeightCm: Number(p.deskHeightCm) || 0,
    postureAngleDeg: Number(p.postureAngleDeg) || 0,
    postureGood: p.postureGood ? 1 : 0,
};

msg.timestamp = p.ts ? new Date(p.ts) : new Date();
return msg;

let p = msg.payload;
if (typeof p === "string") {
    try { p = JSON.parse(p); }
    catch (e) { node.warn("Bad JSON in environment details: " + e.message); return null; }
}
if (!p || !p.deskId || !p.userId) return null;

// Basic values
const presentVal = p.present ? 1 : 0;
const lampVal = p.lampOn ? 1 : 0;
const mode = p.mode || "focus";
const TICK_SEC = 30;

// Rolling timers from flow context
let highCo2Sec = flow.get("highCo2Sec") || 0;
let lowLightSec = flow.get("lowLightSec") || 0;
let postureBadSec = flow.get("postureBadSec") || 0;

// Thresholds (seconds)
const HIGH_CO2_THRESHOLD_SEC = 15 * 60;
const LOW_LIGHT_THRESHOLD_SEC = 5 * 60;
const BAD_POSTURE_THRESHOLD_SEC = 20 * 60;

// Accumulate CO2 exposure
if (Number(p.co2ppm) > 1000) {
    highCo2Sec += TICK_SEC;
} else {
    highCo2Sec = 0;
}

// Accumulate low light while present and lamp off
const LOW_LIGHT_CRITERIA = presentVal && !lampVal && (Number(p.lightLux) < 250);
if (LOW_LIGHT_CRITERIA) {
    lowLightSec += TICK_SEC;
} else {
    lowLightSec = 0;
}

// Accumulate bad posture while present and postureGood=0
const POSTURE_CRITERIA = presentVal && !p.postureGood;
if (POSTURE_CRITERIA) {
    postureBadSec += TICK_SEC;
} else {
    postureBadSec = 0;
}

// Derive warnings
const dimLightWarning = (lowLightSec >= LOW_LIGHT_THRESHOLD_SEC) ? 1 : 0;
const highCo2Warning = (highCo2Sec >= HIGH_CO2_THRESHOLD_SEC) ? 1 : 0;
const badPostureWarning = (postureBadSec >= BAD_POSTURE_THRESHOLD_SEC) ? 1 : 0;

// Build friendly messages for dashboard
let messages = [];
if (dimLightWarning) messages.push("Lighting is low ‚Äî turn on lamp or open blinds.");
if (highCo2Warning) messages.push("CO‚ÇÇ is high ‚Äî open a window or take a fresh air break.");
if (badPostureWarning) messages.push("Posture is poor ‚Äî adjust chair/desk or stand up.");

if (!messages.length) {
    if (presentVal) {
        messages.push(`All good ‚Äî environment and posture look fine in ${mode} mode.`);  // *** FIXED: backticks, not parenthesis ***
    } else {
        messages.push("Desk is idle ‚Äî no user present.");
    }
}

// Save state back to flow
flow.set("highCo2Sec", highCo2Sec);
flow.set("lowLightSec", lowLightSec);
flow.set("postureBadSec", postureBadSec);

// Map to Influx measurement
msg.measurement = "desk_environment";
msg.tags = {
    deskId: p.deskId,
    userId: p.userId,
    mode: mode
};

msg.payload = {
    // Raw environment values
    temperatureC: Number(p.temperatureC) || 0,
    humidityPct: Number(p.humidityPct) || 0,
    co2ppm: Number(p.co2ppm) || 0,
    noiseDb: Number(p.noiseDb) || 0,
    lightLux: Number(p.lightLux) || 0,
    lampOn: lampVal,
    lampPowerW: Number(p.lampPowerW) || 0,

    // Rolling exposure timers
    highCo2Sec,
    lowLightSec,
    postureBadSec,

    // Warnings + narrative
    dimLightWarning,
    highCo2Warning,
    badPostureWarning,
    environmentNotifyText: messages.join(" | ")
};

msg.timestamp = p.ts ? new Date(p.ts) : new Date();
return msg;

let p = msg.payload;
if (typeof p === "string") {
    try { p = JSON.parse(p); }
    catch (e) { node.warn("Bad JSON in status details: " + e.message); return null; }
}
if (!p || !p.deskId || !p.userId) return null;

const TICK_SEC = 30;
const presentVal = p.present ? 1 : 0;
const mode = p.mode || "focus";

// --- DESK HEIGHT INTELLIGENCE ---
const STANDING_THRESHOLD = 90;  // cm
const RECOMMENDED_STANDING_HEIGHT = 110;  // cm

// Determine if user is ACTUALLY sitting based on desk height
const isSitting = Number(p.deskHeightCm) < STANDING_THRESHOLD;
const sitDuration = Number(p.sitStreakMin);

// ---- Rolling focus timer (low focus) ----
let lowFocusSec = flow.get("lowFocusSec") || 0;
if (presentVal && Number(p.focusScore) < 50) {
    lowFocusSec += TICK_SEC;
} else {
    lowFocusSec = 0;
}

// Thresholds
const LONG_SIT_THRESHOLD_MIN = 45;
const LOW_FOCUS_THRESHOLD_SEC = 10 * 60;
const HIGH_CO2_THRESHOLD_SEC = 15 * 60;
const LOW_LIGHT_THRESHOLD_SEC = 5 * 60;

// Warnings (ergonomics & focus)
// CRITICAL: Only warn if ACTUALLY sitting (desk height < 90cm)
const longSitWarning = (sitDuration >= LONG_SIT_THRESHOLD_MIN && isSitting) ? 1 : 0;
const lowFocusWarning = (lowFocusSec >= LOW_FOCUS_THRESHOLD_SEC) ? 1 : 0;
const postureWarning = (presentVal && !p.postureGood && isSitting) ? 1 : 0;

// Retrieve env warnings from flow context
let highCo2Sec = flow.get("highCo2Sec") || 0;
let lowLightSec = flow.get("lowLightSec") || 0;
let postureBadSec = flow.get("postureBadSec") || 0;

const dimLightWarning = (lowLightSec >= LOW_LIGHT_THRESHOLD_SEC) ? 1 : 0;
const highCo2Warning = (highCo2Sec >= HIGH_CO2_THRESHOLD_SEC) ? 1 : 0;

// Save focus timer state
flow.set("lowFocusSec", lowFocusSec);

// --- SMART DESK HEIGHT RECOMMENDATIONS ---
let deskHeightRecommendation = "";
let deskPositionStatus = "";

if (isSitting && sitDuration >= LONG_SIT_THRESHOLD_MIN) {
    // Alert: User should stand
    deskHeightRecommendation = `‚¨ÜÔ∏è RAISE DESK to ${RECOMMENDED_STANDING_HEIGHT} cm and stand!`;
    deskPositionStatus = "üî¥ Alert: Too long sitting";

} else if (isSitting && sitDuration >= 30) {
    // Warning: Approaching threshold
    deskHeightRecommendation = `‚ö†Ô∏è Consider standing soon (desk at ${p.deskHeightCm} cm)`;
    deskPositionStatus = "üü° Warning: Approaching limit";

} else if (!isSitting) {
    // Standing: Positive feedback
    deskHeightRecommendation = `üëè Great! Standing at ${p.deskHeightCm} cm`;
    deskPositionStatus = "‚úÖ Standing - Healthy";

} else {
    // Normal sitting: Comfortable
    deskHeightRecommendation = `‚úÖ Sitting comfortably at ${p.deskHeightCm} cm`;
    deskPositionStatus = "‚úÖ Sitting - Normal";
}

// Build one combined assistant message
let messages = [];

// Ergonomic warnings (with desk height context)
if (longSitWarning) {
    messages.push(`üö® Sitting for ${sitDuration} min! ${deskHeightRecommendation}`);
}

if (postureWarning && isSitting) {
    messages.push(`‚ö†Ô∏è Poor posture at ${p.deskHeightCm} cm ‚Äî adjust chair or stand.`);
}

if (lowFocusWarning) {
    messages.push("üéØ Focus low for 10+ min ‚Äî try a quick reset or walk.");
}

// Environment warnings
if (dimLightWarning) {
    messages.push("üí° Lighting low ‚Äî increase ambient light to reduce eye strain.");
}

if (highCo2Warning) {
    messages.push("ü´Å CO‚ÇÇ high ‚Äî get fresh air or increase ventilation.");
}

// Default message
if (!messages.length) {
    if (presentVal) {
        if (isSitting) {
            messages.push(`All good ‚Äî sitting at ${p.deskHeightCm} cm. Environment balanced.`);
        } else {
            messages.push(`Excellent! Standing at ${p.deskHeightCm} cm. Keep it up!`);
        }
    } else {
        messages.push("Desk is idle ‚Äî no recommendations at this time.");
    }
}

msg.measurement = "desk_status";
msg.tags = {
    deskId: p.deskId,
    userId: p.userId,
    mode: mode
};

msg.payload = {
    present: presentVal,
    mode,
    focusScore: Number(p.focusScore) || 0,
    sitStreakMin: sitDuration,
    postureGood: p.postureGood ? 1 : 0,

    // Derived timers
    lowFocusSec,
    highCo2Sec,
    lowLightSec,
    postureBadSec,

    // Warnings
    longSitWarning,
    lowFocusWarning,
    postureWarning,
    dimLightWarning,
    highCo2Warning,

    // NEW: Desk height intelligence
    isSitting: isSitting ? 1 : 0,
    deskHeightCm: Number(p.deskHeightCm),
    deskPositionStatus: deskPositionStatus,
    deskHeightRecommendation: deskHeightRecommendation,

    // Narrative
    smartDeskAssistant: messages.join(" | ")
};

if (longSitWarning) {
    node.warn(`üö® LONG SIT ALERT! sitStreakMin = ${sitDuration}, deskHeight = ${p.deskHeightCm} cm`);
}

msg.timestamp = p.ts ? new Date(p.ts) : new Date();
return msg;
