// ----- Smart Desk Full Simulator -----
// This function runs every X seconds from your Inject node.
// It generates realistic values for the dashboard.

// Helper: random in range
function rand(min, max) {
    return Math.round(Math.random() * (max - min) + min);
}

// Get or init focus timer state from flow context
let state = flow.get("focusState") || {
    mode: "focus",       // "focus" or "break"
    remainingSec: 25 * 60  // start at 25 minutes
};

// How often this node is triggered (match your Inject interval)
// If your Inject is every 10 seconds, set 10 here:
const TICK_SEC = 10;

// Update focus timer
if (state.mode === "focus") {
    state.remainingSec -= TICK_SEC;
    if (state.remainingSec <= 0) {
        state.mode = "break";
        state.remainingSec = 5 * 60; // 5 min break
    }
} else if (state.mode === "break") {
    state.remainingSec -= TICK_SEC;
    if (state.remainingSec <= 0) {
        state.mode = "focus";
        state.remainingSec = 25 * 60; // back to 25 min focus
    }
}

// Save state back
flow.set("focusState", state);

// Simulate presence: more likely to be present in focus mode
const present = state.mode === "focus" ? (Math.random() < 0.8) : (Math.random() < 0.3);

// Simulate desk metrics
const deskHeightCm = rand(70, 120);          // 70–120 cm
const temperatureC = (Math.random() * 4 + 22).toFixed(1);  // 22–26 °C
const lightLux = rand(200, 500);             // 200–500 lux
const airQualityAQI = rand(50, 120);         // 50–120 AQI
const lampOn = present && lightLux < 350;    // lamp ON if present & relatively dark

// Simulate focusScore: high if present & in focus mode
let focusScore = 0;
if (present && state.mode === "focus") {
    focusScore = rand(60, 95);
} else if (present) {
    focusScore = rand(30, 70);
} else {
    focusScore = 0;
}

// Compute remaining minutes for display
const focusTimerMin = Math.max(0, Math.round(state.remainingSec / 60));

// Build payload
const payload = {
    deskId: "desk01",
    userId: "user01",
    deskHeightCm: deskHeightCm,
    temperatureC: Number(temperatureC),
    lightLux: lightLux,
    airQualityAQI: airQualityAQI,
    present: present,
    lampOn: lampOn,
    focusScore: focusScore,
    focusTimerMin: focusTimerMin,
    mode: state.mode,          // "focus" or "break"
    ts: new Date().toISOString()
};

// Set topic and payload for MQTT
msg.topic = "desk/desk01/telemetry";
msg.payload = JSON.stringify(payload);

return msg;

// ----- Prepare Influx Point for Smart Desk with Notifications -----

let p = msg.payload;

// If payload is a JSON string, parse it
if (typeof p === "string") {
    try {
        p = JSON.parse(p);
    } catch (e) {
        node.warn("Payload is a string but not valid JSON: " + e.message);
        return null;
    }
}

// Validate
if (!p || typeof p !== "object") {
    node.warn("No valid payload object received");
    return null;
}
if (!p.deskId || !p.userId) {
    node.warn("Missing deskId or userId");
    return null;
}

// Convert booleans to numbers for Influx
const presentVal = p.present ? 1 : 0;
const lampVal    = p.lampOn ? 1 : 0;

// ---- Notification state (we assume your simulator tick is every 10s) ----
const TICK_SEC = 10;   // match your Inject interval

let sitSec      = flow.get("sitSec")      || 0;
let lowFocusSec = flow.get("lowFocusSec") || 0;

// 1) Sitting time: accumulate while present
if (p.present) {
    sitSec += TICK_SEC;
} else {
    sitSec = 0; // reset when away
}

// 2) Focus quality: accumulate time where focusScore is low while present
if (p.present && Number(p.focusScore) < 50) {
    lowFocusSec += TICK_SEC;
} else {
    lowFocusSec = 0;
}

// Save back to context
flow.set("sitSec", sitSec);
flow.set("lowFocusSec", lowFocusSec);

// ---- Compute warning flags ----

// Long sitting: > 45 minutes
const LONG_SIT_THRESHOLD_SEC = 2 * 60 //45 * 60;
const longSitWarning = sitSec >= LONG_SIT_THRESHOLD_SEC ? 1 : 0;

// Dim light: present, light < 250 lux, lamp is OFF
const dimLightWarning = (p.present && Number(p.lightLux) < 250 && !p.lampOn) ? 1 : 0;

// Low focus: present & low focus for > 10 minutes
const LOW_FOCUS_THRESHOLD_SEC = 5 * 60;
const lowFocusWarning = (lowFocusSec >= LOW_FOCUS_THRESHOLD_SEC) ? 1 : 0;

// Build a human-readable notification text
let messages = [];
if (longSitWarning) {
    messages.push("You have been sitting for a long time — consider standing up or taking a short walk.");
}
if (dimLightWarning) {
    messages.push("Desk light is low while you are working — turn on the lamp or increase room lighting.");
}
if (lowFocusWarning) {
    messages.push("Your focus has been low for a while — take a short break or consciously refocus.");
}
if (messages.length === 0) {
    messages.push("All good! Posture, light and focus look fine right now.");
}
const notifyText = messages.join(" ");

// ---- Influx mapping ----
msg.measurement = "desk_status";

msg.tags = {
    deskId: p.deskId,
    userId: p.userId,
    mode:   p.mode || "unknown"
};

// Numeric fields + a string field for the message
msg.payload = {
    deskHeightCm: Number(p.deskHeightCm) || 0,
    temperatureC: Number(p.temperatureC) || 0,
    lightLux:     Number(p.lightLux)     || 0,
    airQualityAQI:Number(p.airQualityAQI)|| 0,
    present:      presentVal,
    lampOn:       lampVal,
    focusScore:   Number(p.focusScore)   || 0,
    focusTimerMin:Number(p.focusTimerMin)|| 0,
    longSitWarning:   longSitWarning,
    dimLightWarning:  dimLightWarning,
    lowFocusWarning:  lowFocusWarning,
    notifyText:       notifyText         // string field
};

// Timestamp
msg.timestamp = p.ts ? new Date(p.ts) : new Date();

return msg;
