// ===== UNIFIED Smart Desk Simulator - ALL DATA IN ONE PAYLOAD =====
function rand(min, max) { return Math.round(Math.random() * (max - min) + min); }
function rfloat(min, max, d = 1) { return parseFloat((Math.random() * (max - min) + min).toFixed(d)); }

const REAL_TICK_SEC = 30;
const DEMO_MIN_PER_TICK = 3;
const TICK_SIM_SEC = DEMO_MIN_PER_TICK * 60;

// Desk height constants
const SITTING_HEIGHT_MIN = 72;
const SITTING_HEIGHT_MAX = 82;
const STANDING_HEIGHT_MIN = 105;
const STANDING_HEIGHT_MAX = 115;
const STANDING_THRESHOLD = 90;
const MIN_STAND_DURATION_SEC = 15 * 60;

// Get simulation state
let state = flow.get("focusState") || {
    mode: "focus",
    remainingSec: 25 * 60,
    sitStreakSec: 0,
    standCyclesToday: 0,
    lastResetDate: null,
    wasStanding: true,
    standStartTime: null,
    lastStandingHeight: null,
    alertTriggered: false
};

// Get warning timers
let lowFocusSec = flow.get("lowFocusSec") || 0;
let highCo2Sec = flow.get("highCo2Sec") || 0;
let lowLightSec = flow.get("lowLightSec") || 0;

// Reset stand cycles every day
const today = new Date().toDateString();
if (!state.lastResetDate || state.lastResetDate !== today) {
    state.standCyclesToday = 0;
    state.lastResetDate = today;
    node.warn(`üåÖ New day started! Stand cycles reset.`);
}

// Advance Pomodoro timer
state.remainingSec -= TICK_SIM_SEC;
if (state.remainingSec <= 0) {
    if (state.mode === "focus") {
        state.mode = "break";
        state.remainingSec = 5 * 60;
    } else {
        state.mode = "focus";
        state.remainingSec = 25 * 60;
    }
}

// --- DESK HEIGHT LOGIC ---
let deskHeightCm;
const sitMinutes = state.sitStreakSec / 60;

if (sitMinutes < 45) {
    deskHeightCm = rand(SITTING_HEIGHT_MIN, SITTING_HEIGHT_MAX);
    state.lastStandingHeight = null;
    state.alertTriggered = false;
} else if (sitMinutes >= 45 && sitMinutes < 48) {
    deskHeightCm = rand(SITTING_HEIGHT_MIN, SITTING_HEIGHT_MAX);
    state.lastStandingHeight = null;
    if (!state.alertTriggered) {
        node.warn(`üö® ALERT TRIGGERED! User has been sitting for ${Math.round(sitMinutes)} minutes!`);
        state.alertTriggered = true;
    }
} else if (sitMinutes >= 48) {
    if (state.lastStandingHeight === null) {
        state.lastStandingHeight = rand(STANDING_HEIGHT_MIN, STANDING_HEIGHT_MAX);
        node.warn(`üìà User responding to alert - RAISING DESK to ${state.lastStandingHeight} cm`);
    }
    deskHeightCm = state.lastStandingHeight;
}

let sitStandState = deskHeightCm > STANDING_THRESHOLD ? 1 : 0;

// --- Presence Logic ---
let present;
if (state.sitStreakSec > 0 && sitStandState === 0) {
    present = true;
} else {
    present = Math.random() < (state.mode === "focus" ? 0.8 : 0.3);
}

// --- Auto-reset sit streak after 15 minutes of standing ---
if (state.standStartTime !== null && sitStandState === 1) {
    const standDurationSec = Date.now() / 1000 - state.standStartTime;
    if (standDurationSec >= MIN_STAND_DURATION_SEC && state.sitStreakSec > 0) {
        node.warn(`‚úÖ Stand duration reached ${(standDurationSec / 60).toFixed(1)} min - Sit streak RESET!`);
        state.sitStreakSec = 0;
        state.alertTriggered = false;
    }
}

// --- Sitting Streak Logic ---
if (present && sitStandState === 0) {
    if (state.wasStanding && state.standStartTime !== null) {
        const standDurationSec = Date.now() / 1000 - state.standStartTime;
        if (standDurationSec >= MIN_STAND_DURATION_SEC) {
            state.sitStreakSec = TICK_SIM_SEC;
            state.alertTriggered = false;
            node.warn(`ü™ë Sitting... Streak RESET ‚úÖ (stood for ${(standDurationSec / 60).toFixed(1)} min) (Desk: ${deskHeightCm} cm)`);
        } else {
            state.sitStreakSec += TICK_SIM_SEC;
            node.warn(`ü™ë Sitting again... Streak CONTINUES ‚ö†Ô∏è at ${Math.round(state.sitStreakSec / 60)} min (only stood ${(standDurationSec / 60).toFixed(1)} min) (Desk: ${deskHeightCm} cm)`);
        }
        state.standStartTime = null;
        state.lastStandingHeight = null;
    } else {
        state.sitStreakSec += TICK_SIM_SEC;
        node.warn(`ü™ë Sitting... Streak: ${Math.round(state.sitStreakSec / 60)} min (Desk: ${deskHeightCm} cm)`);
    }
    state.wasStanding = false;
} else if (state.sitStreakSec > 0 && !state.wasStanding && sitStandState === 1) {
    state.standStartTime = Date.now() / 1000;
    state.standCyclesToday += 1;
    state.wasStanding = true;
    state.lastStandingHeight = deskHeightCm;
    node.warn(`‚¨ÜÔ∏è User stood up! Stand cycles: ${state.standCyclesToday} (Desk: ${deskHeightCm} cm) - Timer started!`);
} else if (state.sitStreakSec > 0 && sitStandState === 1) {
    state.wasStanding = true;
}

// --- Generate Sensor Data ---
const temperatureC = rfloat(22, 26, 1);
const lightLux = rand(150, 600);
const humidityPct = rand(35, 60);
const noiseDb = rand(42, 74);
const co2ppm = rand(500, 1400);
const focusScore = present ? (state.mode === "focus" ? rand(60, 95) : rand(30, 70)) : 0;
const keyRatePerMin = present ? (state.mode === "focus" ? rand(80, 220) : rand(10, 120)) : 0;
const screenOn = present ? 1 : 0;
const microbreakDue = (state.mode === "focus" && (state.remainingSec % (10 * 60) < TICK_SIM_SEC)) ? 1 : 0;

// --- ENVIRONMENT PROCESSING ---
// CO2 tracking
if (co2ppm > 1000) {
    highCo2Sec += TICK_SIM_SEC;
} else {
    highCo2Sec = 0;
}

// Light tracking
if (present && lightLux < 250) {
    lowLightSec += TICK_SIM_SEC;
} else {
    lowLightSec = 0;
}

// Focus tracking
if (present && focusScore < 50) {
    lowFocusSec += TICK_SIM_SEC;
} else {
    lowFocusSec = 0;
}

// --- WARNING THRESHOLDS ---
const LONG_SIT_THRESHOLD_MIN = 45;
const LOW_FOCUS_THRESHOLD_SEC = 10 * 60;
const HIGH_CO2_THRESHOLD_SEC = 15 * 60;
const LOW_LIGHT_THRESHOLD_SEC = 5 * 60;

const isSitting = deskHeightCm < STANDING_THRESHOLD;
const sitDuration = Math.round(state.sitStreakSec / 60);

const longSitWarning = (sitDuration >= LONG_SIT_THRESHOLD_MIN && isSitting) ? 1 : 0;
const lowFocusWarning = (lowFocusSec >= LOW_FOCUS_THRESHOLD_SEC) ? 1 : 0;
const dimLightWarning = (lowLightSec >= LOW_LIGHT_THRESHOLD_SEC) ? 1 : 0;
const highCo2Warning = (highCo2Sec >= HIGH_CO2_THRESHOLD_SEC) ? 1 : 0;

// --- DESK HEIGHT RECOMMENDATIONS ---
const RECOMMENDED_STANDING_HEIGHT = 110;
let deskHeightRecommendation = "";
let deskPositionStatus = "";

if (isSitting && sitDuration >= LONG_SIT_THRESHOLD_MIN) {
    deskHeightRecommendation = `‚¨ÜÔ∏è RAISE DESK to ${RECOMMENDED_STANDING_HEIGHT} cm and stand!`;
    deskPositionStatus = "üî¥ Alert: Too long sitting";
} else if (isSitting && sitDuration >= 30) {
    deskHeightRecommendation = `‚ö†Ô∏è Consider standing soon (desk at ${deskHeightCm} cm)`;
    deskPositionStatus = "üü° Warning: Approaching limit";
} else if (!isSitting) {
    deskHeightRecommendation = `üëè Great! Standing at ${deskHeightCm} cm`;
    deskPositionStatus = "‚úÖ Standing - Healthy";
} else {
    deskHeightRecommendation = `‚úÖ Sitting comfortably at ${deskHeightCm} cm`;
    deskPositionStatus = "‚úÖ Sitting - Normal";
}

// --- BUILD MESSAGES ---
let messages = [];
if (longSitWarning) messages.push(`üö® Sitting for ${sitDuration} min! ${deskHeightRecommendation}`);
if (lowFocusWarning) messages.push("üéØ Focus low for 10+ min ‚Äî try a quick reset or walk.");
if (dimLightWarning) messages.push("üí° Lighting low ‚Äî increase ambient light to reduce eye strain.");
if (highCo2Warning) messages.push("ü´Å CO‚ÇÇ high ‚Äî get fresh air or increase ventilation.");

if (!messages.length) {
    if (present) {
        if (isSitting) {
            messages.push(`All good ‚Äî sitting at ${deskHeightCm} cm. Environment balanced.`);
        } else {
            messages.push(`Excellent! Standing at ${deskHeightCm} cm. Keep it up!`);
        }
    } else {
        messages.push("Desk is idle ‚Äî no recommendations at this time.");
    }
}

// --- SAVE STATE ---
flow.set("focusState", state);
flow.set("lowFocusSec", lowFocusSec);
flow.set("highCo2Sec", highCo2Sec);
flow.set("lowLightSec", lowLightSec);

// --- BUILD ONE COMPLETE UNIFIED PAYLOAD ---
const timestamp = new Date().toISOString();

const unifiedPayload = {
    // === METADATA ===
    deskId: "desk01",
    userId: "user01",
    timestamp: timestamp,
    mode: state.mode,

    // === SEATING DATA ===
    seating: {
        present: present ? 1 : 0,
        sitStandState: sitStandState,
        sitStreakMin: sitDuration,
        standCyclesToday: state.standCyclesToday,
        deskHeightCm: deskHeightCm,
        isSitting: isSitting ? 1 : 0,
        deskPositionStatus: deskPositionStatus,
        deskHeightRecommendation: deskHeightRecommendation
    },

    // === ENVIRONMENT DATA ===
    environment: {
        temperatureC: temperatureC,
        humidityPct: humidityPct,
        co2ppm: co2ppm,
        noiseDb: noiseDb,
        lightLux: lightLux,
        highCo2Sec: highCo2Sec,
        lowLightSec: lowLightSec
    },

    // === PRODUCTIVITY DATA ===
    productivity: {
        appFocus: ["IDE", "Browser", "Mail", "Other"][rand(0, 3)],
        screenOn: screenOn,
        focusScore: focusScore,
        keyRatePerMin: keyRatePerMin,
        microbreakDue: microbreakDue,
        focusTimerMin: Math.max(0, Math.round(state.remainingSec / 60)),
        lowFocusSec: lowFocusSec
    },

    // === WARNINGS & RECOMMENDATIONS ===
    alerts: {
        longSitWarning: longSitWarning,
        lowFocusWarning: lowFocusWarning,
        dimLightWarning: dimLightWarning,
        highCo2Warning: highCo2Warning,
        smartDeskAssistant: messages.join(" | ")
    }
};

// For InfluxDB - you can keep this structure or flatten it
msg.topic = "desk/desk01/telemetry";
msg.payload = JSON.stringify(unifiedPayload);

// Calculate payload size
const sizeBytes = Buffer.byteLength(msg.payload, 'utf8');
node.warn(`üì¶ Unified Payload Size: ${sizeBytes} bytes (${(sizeBytes / 1024).toFixed(2)} KB)`);

return msg;

// Extract Seating Data from Unified Payload
let p = msg.payload;
if (typeof p === "string") {
    try { p = JSON.parse(p); }
    catch (e) { node.warn("Parse error: " + e.message); return null; }
}

if (!p || !p.deskId) return null;

msg.measurement = "desk_seating";
msg.tags = {
    deskId: p.deskId,
    userId: p.userId,
    mode: p.mode
};

msg.payload = {
    // Existing fields - PRESERVED
    present: p.seating.present,
    sitStandState: p.seating.sitStandState,
    sitStreakMin: p.seating.sitStreakMin,
    standCyclesToday: p.seating.standCyclesToday,
    deskHeightCm: p.seating.deskHeightCm,
    postureAngleDeg: 0,  // Compatibility field (not simulated yet)
    postureGood: 0,      // Compatibility field (not simulated yet)

    // New fields - ADDITIONS
    isSitting: p.seating.isSitting,
    deskPositionStatus: p.seating.deskPositionStatus,
    deskHeightRecommendation: p.seating.deskHeightRecommendation
};

msg.timestamp = new Date(p.timestamp);
return msg;

// Extract Environment Data from Unified Payload
let p = msg.payload;
if (typeof p === "string") {
    try { p = JSON.parse(p); }
    catch (e) { node.warn("Parse error: " + e.message); return null; }
}

if (!p || !p.deskId) return null;

msg.measurement = "desk_environment";
msg.tags = {
    deskId: p.deskId,
    userId: p.userId,
    mode: p.mode
};

// Build environment message
let envMessages = [];
if (p.alerts.dimLightWarning) envMessages.push("Lighting is low ‚Äî turn on lamp or open blinds.");
if (p.alerts.highCo2Warning) envMessages.push("CO‚ÇÇ is high ‚Äî open a window or take a fresh air break.");
if (!envMessages.length && p.seating.present) {
    envMessages.push(`All good ‚Äî environment looks fine in ${p.mode} mode.`);
}

msg.payload = {
    // Existing fields - PRESERVED
    temperatureC: p.environment.temperatureC,
    humidityPct: p.environment.humidityPct,
    co2ppm: p.environment.co2ppm,
    noiseDb: p.environment.noiseDb,
    lightLux: p.environment.lightLux,
    highCo2Sec: p.environment.highCo2Sec,
    lowLightSec: p.environment.lowLightSec,
    dimLightWarning: p.alerts.dimLightWarning,
    highCo2Warning: p.alerts.highCo2Warning,
    environmentNotifyText: envMessages.join(" | ")  // Compatibility field
};

msg.timestamp = new Date(p.timestamp);
return msg;

// Extract Status & Alerts Data from Unified Payload
let p = msg.payload;
if (typeof p === "string") {
    try { p = JSON.parse(p); }
    catch (e) { node.warn("Parse error: " + e.message); return null; }
}

if (!p || !p.deskId) return null;

msg.measurement = "desk_status";
msg.tags = {
    deskId: p.deskId,
    userId: p.userId,
    mode: p.mode
};

msg.payload = {
    // Productivity metrics
    present: p.seating.present,
    focusScore: p.productivity.focusScore,
    screenOn: p.productivity.screenOn,
    keyRatePerMin: p.productivity.keyRatePerMin,
    microbreakDue: p.productivity.microbreakDue,
    focusTimerMin: p.productivity.focusTimerMin,
    appFocus: p.productivity.appFocus,

    // Derived timers
    lowFocusSec: p.productivity.lowFocusSec,
    highCo2Sec: p.environment.highCo2Sec,
    lowLightSec: p.environment.lowLightSec,

    // All warnings
    longSitWarning: p.alerts.longSitWarning,
    lowFocusWarning: p.alerts.lowFocusWarning,
    dimLightWarning: p.alerts.dimLightWarning,
    highCo2Warning: p.alerts.highCo2Warning,

    // Desk intelligence
    isSitting: p.seating.isSitting,
    deskHeightCm: p.seating.deskHeightCm,
    sitStreakMin: p.seating.sitStreakMin,
    deskPositionStatus: p.seating.deskPositionStatus,
    deskHeightRecommendation: p.seating.deskHeightRecommendation,

    // Master narrative
    smartDeskAssistant: p.alerts.smartDeskAssistant
};

msg.timestamp = new Date(p.timestamp);
return msg;

let payloadString = typeof msg.payload === 'string'
    ? msg.payload
    : JSON.stringify(msg.payload);

let p = msg.payload;
if (typeof p === "string") {
    p = JSON.parse(p);
}

// Calculate size in bytes
let sizeInBytes = Buffer.byteLength(payloadString, 'utf8');
let sizeInKB = (sizeInBytes / 1024).toFixed(2);

// Log it
node.warn(`üì¶ Payload Size: ${sizeInBytes} bytes (${sizeInKB} KB) unified payload ${JSON.stringify(p, null, 2)}`);

// Store in message for later use
msg.payloadSize = sizeInBytes;
msg.payloadSizeKB = sizeInKB;

// Add to context for tracking
let totalSize = context.get('totalSize') || 0;
let messageCount = context.get('messageCount') || 0;

totalSize += sizeInBytes;
messageCount += 1;

context.set('totalSize', totalSize);
context.set('messageCount', messageCount);

let avgSize = (totalSize / messageCount).toFixed(2);
node.warn(`üìä Average: ${avgSize} bytes | Total: ${messageCount} messages`);

return msg;
